@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222, 47%, 11%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 47%, 11%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 47%, 11%);
  --primary: hsl(188, 94%, 43%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(258, 90%, 66%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215, 16%, 47%);
  --accent: hsl(326, 78%, 65%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(188, 94%, 43%);
  --chart-1: hsl(188, 94%, 43%);
  --chart-2: hsl(159, 78%, 36%);
  --chart-3: hsl(42, 93%, 56%);
  --chart-4: hsl(147, 79%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --sidebar: hsl(0, 0%, 100%);
  --sidebar-foreground: hsl(222, 47%, 11%);
  --sidebar-primary: hsl(188, 94%, 43%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 40%, 96%);
  --sidebar-accent-foreground: hsl(188, 94%, 43%);
  --sidebar-border: hsl(214, 32%, 91%);
  --sidebar-ring: hsl(188, 94%, 43%);
  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 1rem;
}

.dark {
  --background: hsl(222, 47%, 11%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(217, 33%, 17%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(217, 33%, 17%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(188, 94%, 43%);
  --primary-foreground: hsl(222, 47%, 11%);
  --secondary: hsl(258, 90%, 66%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 33%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(326, 78%, 65%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(217, 33%, 25%);
  --input: hsl(217, 33%, 25%);
  --ring: hsl(188, 94%, 43%);
}

@layer base {
  * {
    @apply border-border;
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    min-height: 100vh;
  }

  body:not(.dark) {
    background: radial-gradient(ellipse at top, hsl(188, 94%, 95%) 0%, hsl(210, 40%, 98%) 40%, hsl(214, 32%, 95%) 100%);
  }

  body.dark {
    background: radial-gradient(ellipse at top, hsl(188, 94%, 15%) 0%, hsl(222, 47%, 11%) 40%, hsl(222, 47%, 8%) 100%);
  }
}

@layer utilities {
  .gradient-text {
    background: linear-gradient(135deg, var(--primary) 0%, var(--secondary) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-bg {
    background: linear-gradient(135deg, var(--primary) 0%, var(--secondary) 100%);
  }

  .gradient-border {
    position: relative;
    background: var(--card);
    border-radius: 1rem;
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 1rem;
    padding: 2px;
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .hover-lift {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .hover-lift:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(6, 182, 212, 0.2);
  }

  .timeline-line {
    position: absolute;
    left: 1.5rem;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(to bottom, var(--primary), var(--secondary));
  }

  .skill-badge {
    transition: all 0.3s ease;
  }

  .skill-badge:hover {
    transform: scale(1.05);
    background: linear-gradient(135deg, var(--primary), var(--secondary));
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  @keyframes fadeIn {
    0% { opacity: 0; }
    100% { opacity: 1; }
  }

  @keyframes slideUp {
    0% { 
      transform: translateY(20px);
      opacity: 0;
    }
    100% { 
      transform: translateY(0);
      opacity: 1;
    }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.15);
  }

  .dark .glass-card {
    background: rgba(29, 40, 58, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.4);
  }

  .profile-glow {
    position: relative;
  }

  .profile-glow::before {
    content: '';
    position: absolute;
    inset: -10px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--primary), var(--secondary), var(--accent));
    opacity: 0.3;
    filter: blur(20px);
    animation: pulse-glow 3s ease-in-out infinite;
    z-index: -1;
  }

  @keyframes pulse-glow {
    0%, 100% { opacity: 0.3; transform: scale(1); }
    50% { opacity: 0.5; transform: scale(1.05); }
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.8s ease-out;
  }

  .animate-slide-in-right {
    animation: slideInRight 0.8s ease-out;
  }

  .writing-vertical-rl {
    writing-mode: vertical-rl;
    text-orientation: mixed;
  }


  .animate-fly-in {
    animation: fly-in-from-side 0.8s ease-out;
  }

  @keyframes fly-in-from-side {
    0% {
      transform: translateX(-300px);
      opacity: 0;
    }
    70% {
      transform: translateX(10px);
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  .animate-slide-from-left {
    animation: slideFromLeft 0.8s ease-out;
    animation-fill-mode: both;
  }

  .animate-slide-from-right {
    animation: slideFromRight 0.8s ease-out;
    animation-fill-mode: both;
  }

  @keyframes slideFromLeft {
    0% {
      transform: translateX(-150px);
      opacity: 0;
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  @keyframes slideFromRight {
    0% {
      transform: translateX(150px);
      opacity: 0;
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  @keyframes slideInLeft {
    0% {
      transform: translateX(-100px);
      opacity: 0;
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  @keyframes slideInRight {
    0% {
      transform: translateX(100px);
      opacity: 0;
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  .section-card {
    @apply glass-card rounded-2xl p-6 hover-lift;
  }

  .center-stage {
    min-height: calc(100vh - 64px);
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    padding: 4rem 1rem;
  }

  @media (min-width: 1024px) {
    .center-stage {
      grid-template-columns: 1fr auto 1fr;
      grid-template-areas: 
        "left center right";
      align-items: start;
      padding: 6rem 2rem;
    }
  }
}

